import React, { useState } from 'react';
import axios from 'axios'
import ReactTable from "react-table";

const Bank = () => {

    let staticData = {
        "symbols": [
            {
                "symbol": "HDFC",
                "symbol_info": "Housing Development Finance Corporation Limited",
                "symbol_suggest": [
                    {
                        "input": "HDFC",
                        "weight": 16
                    },
                    {
                        "input": "Housing",
                        "weight": 8
                    },
                    {
                        "input": "Development",
                        "weight": 8
                    },
                    {
                        "input": "Finance",
                        "weight": 8
                    },
                    {
                        "input": "Corporation",
                        "weight": 8
                    },
                    {
                        "input": "Limited",
                        "weight": 8
                    }
                ],
                "result_type": "symbol",
                "result_sub_type": "equity",
                "activeSeries": [
                    "EQ",
                    "W3"
                ],
                "url": "/get-quotes/equity?symbol=HDFC"
            },
            {
                "symbol": "HDFCBANK",
                "symbol_info": "HDFC Bank Limited",
                "symbol_suggest": [
                    {
                        "input": "HDFCBANK",
                        "weight": 16
                    },
                    {
                        "input": "HDFC",
                        "weight": 8
                    },
                    {
                        "input": "Bank",
                        "weight": 8
                    },
                    {
                        "input": "Limited",
                        "weight": 8
                    }
                ],
                "result_type": "symbol",
                "result_sub_type": "equity",
                "activeSeries": [
                    "EQ"
                ],
                "url": "/get-quotes/equity?symbol=HDFCBANK"
            },
            {
                "symbol": "HDFCLIFE",
                "symbol_info": "HDFC Life Insurance Company Limited",
                "symbol_suggest": [
                    {
                        "input": "HDFCLIFE",
                        "weight": 16
                    },
                    {
                        "input": "HDFC",
                        "weight": 8
                    },
                    {
                        "input": "Life",
                        "weight": 8
                    },
                    {
                        "input": "Insurance",
                        "weight": 8
                    },
                    {
                        "input": "Company",
                        "weight": 8
                    },
                    {
                        "input": "Limited",
                        "weight": 8
                    }
                ],
                "result_type": "symbol",
                "result_sub_type": "equity",
                "activeSeries": [
                    "EQ"
                ],
                "url": "/get-quotes/equity?symbol=HDFCLIFE"
            },
            {
                "symbol": "HDFCAMC",
                "symbol_info": "HDFC Asset Management Company Limited",
                "symbol_suggest": [
                    {
                        "input": "HDFCAMC",
                        "weight": 11
                    },
                    {
                        "input": "HDFC",
                        "weight": 6
                    },
                    {
                        "input": "Asset",
                        "weight": 6
                    },
                    {
                        "input": "Management",
                        "weight": 6
                    },
                    {
                        "input": "Company",
                        "weight": 6
                    },
                    {
                        "input": "Limited",
                        "weight": 6
                    }
                ],
                "result_type": "symbol",
                "result_sub_type": "equity",
                "activeSeries": [
                    "EQ"
                ],
                "url": "/get-quotes/equity?symbol=HDFCAMC"
            }
        ]
    }

    const [inputVal, setInputVal] = useState([]);
    const [matchedRecord, setMatchedRecord] = useState([]);

    let vallll = []
    const inputHandler = (e) => {
        let currentVal = e?.target?.value;
        let data = staticData.symbols.filter((item) => {
            return item.symbol === currentVal.toUpperCase()
        })
        if (data?.length > 0) {
            setInputVal(data)
        }
    }


    const addHandler = () => {
        if(inputVal){
            setMatchedRecord(inputVal)
        }
    }


console.log("vallll",matchedRecord)

    return (
        <>
            <input type="search" onChange={(e) => inputHandler(e)} />
            <button onClick={() => addHandler()}>Add</button>

            {matchedRecord && matchedRecord?.length > 0 && matchedRecord?.map((item, index) => {
                return (
                    <div key={index}>
                        <table>
                            <tr>
                                <th>Name</th>
                                <th>Type</th>
                                <th>Industry</th>
                            </tr>
                            <tr>
                                <td>{item.symbol}</td>
                                <td>{item.symbol_info}</td>
                                <td>{item.result_sub_type}</td>
                            </tr>
                        </table>
                    </div>
                )
            })
            }
            {matchedRecord && matchedRecord?.length === 0 && <div>No Record Found</div>}
        </>
    )
}
export default Bank;
