import React, { useRef, useState } from 'react';
import '../../../css/custom.css'

const Draw = () => {

    const canvasRef = useRef(null);
    const ctxRef = useRef(null);
    const [drawing, setDrawing] = useState(false);

    const startDraw = ({ event }) => {
        const { offsetX, offsetY } = event;
        ctxRef.current.beginPath();
        ctxRef.current.moveTo(offsetX, offsetY);
        setDrawing(true);
    }

    const stopDraw = () => {
        ctxRef.current.closePath();
        setDrawing(false);
    }

    const draw = (event) => {
        if (!drawing) {
            return;
        }
        const { offsetX, offsetY } = event;
        ctxRef.current.lineTo(offsetX, offsetY);
        ctxRef.current.stroke();

    }

    const clearCanvas = () => {
        ctxRef.current.clearRect(0, 0, ctxRef.current.canvas.width, ctxRef.current.canvas.height);

    }


    // Import, state and ref events

    useEffect(() => {
        const canvas = canvasRef.current;
        // For supporting computers with higher screen densities, we double the screen density
        canvas.width = window.innerWidth * 2;
        canvas.height = window.innerHeight * 2;
        canvas.style.width = `${window.innerWidth}px`;
        canvas.style.height = `${window.innerHeight}px`;
        // Setting the context to enable us draw
        const ctx = canvas.getContext('2d');
        ctx.scale(2, 2);
        ctx.lineCap = 'round';
        ctx.strokeStyle = 'blue';
        ctx.lineWidth = 20;
        ctxRef.current = ctx;
    }, []);

    // Drawing functionalities


    console.log("canvasRef", drawing);

    return (
        <>
            <canvas id="canvas"
                onMouseDown={startDraw}
                onMouseUp={stopDraw}
                onMouseMove={draw}
                ref={canvasRef}
            />
            hisk
            {/* <script src="index.js"></script> */}
        </>
    )
}
export default Draw;
