import React from 'react';
import "../../../css/lesson1.css";

const LetterShape = () => {


    // const preUnicode = `\000`
    let alphabetsList = [{ label: "a", value: `c05` }, { label: "aa", value: "c06" }, { label: "i", value: "c07" }];

    // const nextLetter = () => {
        let letter = document.getElementById("next-letter-btn");
        let letterDesc = document.querySelector(".letter-desc");
        let letterA = document.querySelector(".letter-a");
        let currentLetterIndex = alphabetsList?.findIndex((letter) => letter.value === letterA.innerHTML);
        letter.addEventListener("click", () => {
            currentLetterIndex++;
            if (currentLetterIndex < alphabetsList.length) {
                letterDesc.innerHTML = alphabetsList[currentLetterIndex].label;
                letterA.innerHTML = alphabetsList[currentLetterIndex].value;
            }
        });
    // }
    return (
        <>
            <h1>Let's understand how to write Telugu Alphabets</h1>
            <div className='letters'>
                <div className='letter-desc'></div>
                <div className='letter-a'>{alphabetsList[0].value}</div>
                <button id="next-letter-btn" onClick={() => nextLetter()}>Next</button>
            </div>
        </>
    )
}
export default LetterShape;
